import React from 'react';
import './index.css';
import { withRouter } from 'react-router-dom'
import main_characters from './res/concept-art/main-characters.png';

class PreviewWindow extends React.Component {
    render () {
        return (
            <div className="preview-window">
                <CloseMenuButton onClick={() => this.props.onClose()}/>
                <img src={this.props.image} alt={this.props.alt}/>
            </div>
        )
    }
}


class ConceptArt extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          previewImage: null
        };
    }

    onClosePreviewWindow() {
        this.setState({
            previewImage: null
        })
    }

    render() {
      return (
        <div>
            {this.state.previewImage && <PreviewWindow onClose={() => this.onClosePreviewWindow()}/>}
            {!this.state.previewImage && <div className='main'>
                <h1 className="banner-title-text">Concept Art</h1>
                <img className="concept-art-piece" src={main_characters} alt="main characters"/>

                <div v-if="previewImage"></div>
            </div>}
        </div>
      )
    }
  }

  export default withRouter(ConceptArt);
