import React from 'react';
import './index.css';
import { withRouter } from 'react-router-dom'
import email_logo from './res/email-logo.png';
import twitter_logo from './res/twitter-logo.png';

var SubPageEnum = {
  HOME: "Home",
  CHARACTERS: "Characters",
  CONCEPT_ART: "Concept Art",
  COMICS: "Comics"
};

class HamburgerMenu extends React.Component {
  render() {
      return (
          <button className="icon-border" onClick={() => this.props.onClick()}>
          <div className="icon-border__line"></div>
          <div className="icon-border__line"></div>
          <div className="icon-border__line"></div>
          </button>
      )
  }
}

class CloseMenuButton extends React.Component {
  render() {
      return (
          <button className="icon-border" onClick={() => this.props.onClick()}>
          <div className="icon-border__close">X</div>
          </button>
      )
  }
}

class Menu extends React.Component {
  render() {
      return (
          <div className="menu">
              <div className="menu__outside" onClick={this.props.onClickOutside}></div>
              <div className="menu__inside">
                  <SubPageButton
                      isSelectedSubPage={this.props.selectedSubPage === SubPageEnum.HOME}
                      title={SubPageEnum.HOME}
                      onClickProp={() => this.props.onClickSubpageButton(SubPageEnum.HOME, '/')}
                  />
                  <SubPageButton
                      isSelectedSubPage={this.props.selectedSubPage === SubPageEnum.CHARACTERS}
                      title={SubPageEnum.CHARACTERS}
                      onClickProp={() => this.props.onClickSubpageButton(SubPageEnum.CHARACTERS, '/characters')}
                  />
                  <SubPageButton
                      isSelectedSubPage={this.props.selectedSubPage === SubPageEnum.CONCEPT_ART}
                      title={SubPageEnum.CONCEPT_ART}
                      onClickProp={() => this.props.onClickSubpageButton(SubPageEnum.CONCEPT_ART, '/concept_art')}
                  />
                  <SubPageButton
                      isSelectedSubPage={this.props.selectedSubPage === SubPageEnum.COMICS}
                      title={SubPageEnum.COMICS}
                      onClickProp={() => this.props.onClickSubpageButton(SubPageEnum.COMICS, '/comics')}
                  />
              </div>
          </div>
      )
  }
}

class SubPageButton extends React.Component {
  render() {
      return (
      <button
          className={`subpage-button ${this.props.isSelectedSubPage && "subpage-button--selected"}`}
          onClick={this.props.onClickProp}
      >
          {this.props.title}
      </button>
      )
  }
}

class ContactInfo extends React.Component {
  render() {
    return (
      <div className="contact-info">
        {this.props.widgets}
      </div>
    )
  }
}

class ContactWidget extends React.Component {
  render() {
    return (
      <a href={this.props.link}>
        <img src={this.props.source} alt={this.props.alt} className="contact-widget"/>
      </a>
    )
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isMenuOpen: false,
      selectedSubPage: SubPageEnum.HOME
    };
  }

  showMenu() {
      this.setState({
          isMenuOpen: true
      })
  }

  hideMenu() {
      this.setState({
          isMenuOpen: false
      })
  }

  onSubPageButtonClick(subpage, path) {
      this.setState({
          isMenuOpen: false,
          selectedSubPage: subpage
      })
      this.props.history.push(path)
  }

  render() {
    return (
      <div>
        {this.state.isMenuOpen &&
          <Menu
              selectedSubPage = {this.state.selectedSubPage}
              onClickOutside = {() => this.hideMenu()} 
              onClickSubpageButton = {(page, path) => this.onSubPageButtonClick(page, path)}
          />
          }
          {!this.state.isMenuOpen && <HamburgerMenu onClick = {() => this.showMenu()}/>}
          {this.state.isMenuOpen && <CloseMenuButton onClick = {() => this.hideMenu()}/>}
        </div>
    )
  }
}

export default withRouter(App);
